import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
	categoriesItemContainer: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
		backgroundColor: "00FFFFFF",
	},

	categoriesPhoto: {
		width: "100%",
		height: 200,
		alignItems: "center",
		justifyContent: "center",
	},
	gradientPhoto: {
		backgroundColor: "rgba(40,40,40,0.3)",
		height: "100%",
		width: "100%",
		justifyContent: "center",
		alignItems: "center",
	},
	categoriesName: {
		fontSize: 20,
		fontWeight: "bold",
		textAlign: "center",
		color: "#ffffff",
	},
});

export default styles;
