	},
	categoriesName: {
		flex: 1,
		fontSize: 20,
		fontWeight: "bold",
		textAlign: "center",
		color: "#ffffff",
		marginTop: 8,
	},
	categoriesInfo: {
		marginTop: 3,
		marginBottom: 5,
		color: "#ffffff",
	},
});

export default styles;
